mapper.update:
update group_buy_activity
activity_name = #{activityName}, discount_id = #{discountId}, group_type = #{groupType}, take_limit_count = #{takeLimitCount}, target = #{target}, valid_time = #{validTime}, status = #{status}, start_time = #{startTime}, end_time = #{endTime}, tag_id = #{tagId}, tag_scope = #{tagScope}, update_time = now()
where activity_id = #{activityId}
